function bubbleSort(arr){
    const n = arr.length;
    let swapped;
    do {
        swapped = false; 
        let count = 0;
        for(let i = 0; i < n-1 ; i++){
            if(arr[i] > arr[i +1] ){
                [arr[i], arr[i + 1] ] = [ arr[i+1], arr[i] ];
                swapped = true;
                console.log(swapped);
                console.log(arr[i]);
            }
            count++;
        }
        console.log(count, "count");
    } while(swapped);
    console.log(arr, "arr");
    return arr;
}

